import React, {useState} from 'react'

export default function Textarea(props) {
    const [text, settext]=useState(""); 
   
    
   
    const handelUpcase =() =>{
      let upperText = text.toUpperCase()
      settext(upperText)
   
  }
    const handelLowercase =() =>{
      let lowerCase = text.toLowerCase()
      settext(lowerCase)
   
  }
    const handelonChange =(event) =>{
        console.log("onchane")
        settext(event.target.value)
      }
      const clearScreen =()=>{
        let newText = '';
        settext(newText)
      }
      
      return (
    <div>
        <div>
            <h1 className="Sigli">{props.tital}</h1>
      <textarea className='mx-96 my-4 bg-black/5 ' id="textfield" value={text} onChange={handelonChange} name="Textarea" rows="10" placeholder='Enter text here' cols="100">
      </textarea>
        </div>
      <button className='p-2 border-2 border-slate-800' onClick={handelUpcase}> Uppercase</button>
      <button onClick={clearScreen}>Clear</button>
      <button onClick={handelLowercase}> LowerCase</button>
      <button> Nospace</button>
      <p>characters: {text.length}</p>
      <p>Words: {text.split(" ").length}</p>
      <p>cap{}</p>
      <h3>Preview </h3>
      <span>{text}</span>
      <br />
      <span>first latter is {text.substring(0,1).toUpperCase()}</span>
    </div>
  )
}
